CREATE TABLE inscricao(
    id BIGSERIAL,
    cpf CHARACTER VARYING(256) NOT NULL,
    nome CHARACTER VARYING(256) NOT NULL,
    sobrenome CHARACTER VARYING(256) NOT NULL,
    sexo CHAR(1) NOT NULL,
    email CHARACTER VARYING(256) NOT NULL,
    data_nascimento DATE NOT NULL,
    naturalidade CHARACTER VARYING(256),
    nome_mae CHARACTER VARYING(256),
    nome_pai CHARACTER VARYING(256),
    estado_civil CHARACTER VARYING(256) NOT NULL,
    telefone_fixo CHARACTER VARYING(30),
    telefone_celular CHARACTER VARYING(30),
    telefone_alternativo CHARACTER VARYING(30),
    cep CHAR(8) NOT NULL,
    endereco CHARACTER VARYING(256) NOT NULL,
    numero CHARACTER VARYING(30) NOT NULL,
    complemento CHARACTER VARYING(256),
    cidade CHARACTER VARYING(256) NOT NULL,
    estado CHAR(2) NOT NULL,
    cargo_atual CHARACTER VARYING(256),
    empresa CHARACTER VARYING(256),
    telefone_comercial CHARACTER VARYING(30),
    status SMALLINT NOT NULL DEFAULT 0,
    curso_id BIGINT NOT NULL,
    documento_cpf VARCHAR,
    documento_rg VARCHAR,
    documento_diploma VARCHAR,
    documento_comprovante_residencia VARCHAR,
    documento_curriculo VARCHAR,
    documento_justificativa VARCHAR,
    whatsapp CHARACTER VARYING(50),
    skype CHARACTER VARYING(256),
    tipo_identidade CHARACTER VARYING(256),
    identidade CHARACTER VARYING(256),
    orgao_expedidor CHARACTER VARYING(256),
    habilitacao1 BIGINT,
    habilitacao2 BIGINT,
    candidato_a_bolsa BOOLEAN DEFAULT FALSE,
    recebe_bolsa BOOLEAN DEFAULT FALSE,
    modalidade VARCHAR,
    comentarios TEXT,
    observacoes TEXT,
    data_matricula DATE,
    status_aluno CHARACTER VARYING(256),   -- campo utilizado quando o aluno é matriculado
    turma INTEGER DEFAULT 1,
    CONSTRAINT inscricao_pk PRIMARY KEY (id)
);
COMMENT ON COLUMN inscricao.status IS '0 = pendente, 1 = pagou pré-inscrição (status default porque pessoas não podem pagar pré-inscrição), 2 = documentos validados, 3 = matrícula paga';

