--------------------------------------------------------------------------------
-- Tabelas do módulo Rights
--

--------------------------------------------------------------------------------
-- Table: authitem
-- Parte do módulo Rights. Cada authitem corresponde a uma operação atômica
-- (acesso a um método de um controller) que um usuário pode realizar

CREATE TABLE authitem
(
    name character varying(64) NOT NULL,
    type integer NOT NULL,
    description text,
    bizrule text,
    data text,
    CONSTRAINT authitem_pkey PRIMARY KEY (name)
);

--------------------------------------------------------------------------------
-- Table: authitemchild
-- Parte do módulo Rights. Guarda as permissões a que cada papel tem acesso

CREATE TABLE authitemchild
(
    parent character varying(64) NOT NULL,
    child character varying(64) NOT NULL,
    CONSTRAINT authitemchild_pkey PRIMARY KEY (parent , child),
    CONSTRAINT authitemchild_child_fkey FOREIGN KEY (child)
        REFERENCES authitem (name) MATCH SIMPLE
        ON UPDATE CASCADE ON DELETE CASCADE,
    CONSTRAINT authitemchild_parent_fkey FOREIGN KEY (parent)
        REFERENCES authitem (name) MATCH SIMPLE
        ON UPDATE CASCADE ON DELETE CASCADE
);

--------------------------------------------------------------------------------
-- Table: authassignment
-- Parte do módulo Rights. Define e faz a relação entre papéis do sistema e os
-- usuários

CREATE TABLE authassignment
(
    itemname character varying(64) NOT NULL,
    userid character varying(64) NOT NULL,
    bizrule text,
    data text,
    CONSTRAINT authassignment_pkey PRIMARY KEY (itemname , userid),
    CONSTRAINT authassignment_itemname_fkey FOREIGN KEY (itemname)
        REFERENCES authitem (name) MATCH SIMPLE
        ON UPDATE CASCADE ON DELETE CASCADE
);

--------------------------------------------------------------------------------
-- Table: rights
-- Parte do módulo Rights.

CREATE TABLE rights
(
    itemname character varying(64) NOT NULL,
    type integer NOT NULL,
    weight integer NOT NULL,
    CONSTRAINT rights_pkey PRIMARY KEY (itemname),
    CONSTRAINT rights_itemname_fkey FOREIGN KEY (itemname)
        REFERENCES authitem (name) MATCH SIMPLE
        ON UPDATE CASCADE ON DELETE CASCADE
);



CREATE OR REPLACE FUNCTION remover_documentos_de_usuario(cpf_remover VARCHAR(30))
RETURNS void as $$
BEGIN
    UPDATE inscricao SET
        documento_cpf = NULL,
        documento_rg = NULL,
        documento_diploma = NULL,
        documento_comprovante_residencia = NULL,
        documento_curriculo = NULL,
        documento_justificativa = NULL
    WHERE cpf = cpf_remover;
END;
$$ LANGUAGE plpgsql;

